/* ---------------------*/
/* Der Detail-Block mit */ 
/* einigen Anpassungen  */
/* ---------------------*/
summary {
  list-style: none;
  display: flex; /* also removes the list marker */
  align-items: center;
  padding-left: 0.7rem;
  margin-bottom: 0.5rem;
  color: var(--wp--preset--color--base-2);
  background: var(--wp--preset--color--dunkelblau);
  border-radius: 5px;
}

summary::before{
  content: '>';
  transition: 0.2s;
  margin-right: 1rem;
  font-size: 2rem;
}


details.wp-block-details.is-layout-flow.wp-block-details-is-layout-flow {
    margin-block-start: 20px;
}

details[open] > summary::before{
  transform: rotate(90deg);
}

.wp-block-details[open] summary,
.wp-block-details summary:hover
{
    background: var(--wp--preset--color--custom-dunkleres-orange);
    transition: all 0.2s ease-in-out;
}

.wp-block-details summary:focus {
    color: var(--wp--preset--color--contrast);
    background: var(--wp--preset--color--hellblau);
    transition: all 0.2s ease-in-out;
}

summary::-webkit-details-marker {
    display: none;
}

.wp-block-details summary:focus {
    outline-style: none;
}

/* Background content of details */
details p, details ol.wp-block-list, details ul.wp-block-list {
    background: var(--wp--preset--color--base);
    padding: 2rem;
    margin-top: 0;
}

