add_action( 'post_thumbnail_html', 'custom_description', 10 );
function custom_description( $html ) {
    if ( is_single() ){
	  $beschriftung = get_post(get_post_thumbnail_id())->post_excerpt;
	  if ($beschriftung != '') { 
		$html .= '<p class="em_bild">' . $beschriftung . '</p>';
	  }
    }
    return $html;
}

add_filter('the_content', 'filter_gpx_links' );
function filter_gpx_links( $content ) {
  $content = preg_replace('/<a(.*)href="([^"]*\.gpx)"(.*)>/','<a$1href="$2" download="$2" $3>', $content);
  return $content;
}

function my_myme_types($mime_types){
    $mime_types['gpx'] = 'application/gpx+xml'; //Adds gpx extension
    return $mime_types;
}
add_filter('upload_mimes', 'my_myme_types', 1, 1);
